INSERT INTO `cpl_options` VALUES("205", "headway_|template=white-wash|_option_group_general", "a:6:{s:8:\"live-css\";s:2017:\"/** Logo Adjustments **/\n.block-type-header span.banner a {\n  float: left;\n}\n\n/** Logo Navigation Adjustments **/\n\n#block-2.block-type-navigation ul {\n  text-align: right;\n}\n\n.block-type-navigation ul.sub-menu {\n  margin-left: 5px;\n}\n/** Content block Adjustments **/\n.block-type-content div.entry-meta span.separator {\n  color: #ccc;\n  padding: 0 4px\n}\n\n.block-type-content div.entry-utility-below strong {\n  font-size: 14px;\n  position: relative;\n  top: -5px;\n}\n\n.block-type-content div.entry-content a.more-link {\n  text-indent: -9999px;\n  display:block;\n  float:right;\n  width: 40px;\n  height: 40px;\n}\n\n.block-type-content div.entry-content hr {\n  border-top: 1px solid #ddd;\n  border-bottom: none;\n}\n\n\n/** Page title block - inner pages **/\na.action-button.primary.pagetitle {\n  height: 23px;\n  bottom: 0;\n  top: 0;\n  right: 0;\n  position: absolute;\n  margin: auto; \n}\n\nh1.page-title  {\n  padding-right: 280px;\n}\n\n/** Footer Adjustments **/\n.block-type-footer a.footer-go-to-top-link {\n  text-indent: -9999px;\n  display:block;\n}\n\n/** Grayscale Images **/\n.block-type-content a.post-thumbnail img {\n  filter: gray;\n  -webkit-filter: grayscale(1); \n  -webkit-filter: grayscale(100%);\n  -moz-filter: grayscale(100%);\n  filter: grayscale(100%);\n  -webkit-transition: 1.5s ease;\n  -moz-transition: 1.5s ease;\n  -o-transition: 1.5s ease;\n  transition: 1.5s ease;\n}\n\n.block-type-content a.post-thumbnail img:hover {\n  filter: none;\n  -webkit-filter: grayscale(0); \n  -webkit-filter: grayscale(0%);\n  -moz-filter: grayscale(0%);\n  filter: grayscale(0%);\n  -webkit-transition: 1.5s ease;\n  -moz-transition: 1.5s ease;\n  -o-transition: 1.5s ease;\n  transition: 1.5s ease;\n}\n\n/*------ Typography & Common Classes ------*/\n\n/** Action Buttons **/\n\na.action-button.primary,\na.action-button.se